void assets_init()
{
   font = SLK_rgb_sprite_create(128,48);
   memcpy(font->data,font_data,sizeof(*font->data)*128*48);
   SLK_draw_rgb_set_font_sprite(font);
}

SLK_Palette *assets_load_pal_default()
{
   SLK_Palette *p = malloc(sizeof(*p));
   int size = sizeof(default_palette)/sizeof(SLK_Color);
   p->used = size;
   memcpy(p->colors,default_palette,size*sizeof(SLK_Color));

   return p;
}
